import axios from "axios"
import { useState, useEffect } from "react"

const Category = () => {

    const [data, setData] = useState();
    const [currentPage, setCurrentPage] = useState(0);
    const [userMessage, setUserMessage] = useState("");
    const [userSearchInput, setUserSearchInput] = useState("");
    const itemsPerPage = 16;
    const indexOfLastItem = (currentPage + 1) * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;

    const getTag = () => {
        const rawURL = window.location.pathname
        const tag = rawURL.split("/")[2]
        return tag
    }

    useEffect(() => {
        axios.get("http://localhost:8000/post/tags/" + getTag())
            .then((res) => setData(res.data)).catch((err) => console.error(err))
    }, []);



    const handlePageClick = (selectedPage) => {
        setCurrentPage(selectedPage.selected);
        UseScroll("#root", "#pagination", 1000, "easeOutQuint");
    };

    function getRandomInt(max) {
        return Math.floor(Math.random() * max);
    }


    useEffect(() => {
        axios.get("http://localhost:8000/post/tags")
            .then((res) => {
                const newData = Object.keys(res.data).map((key) => ({
                    tag: key,
                    img: [...res.data[key]]
                }));

                setData(newData);

            })
            .catch((err) => console.error("Error fetching data:", err));
    }, []);


    console.log(data)

    return (
        <main>
            <div>
                {data && data.map((i, index) => (
                    <div key={index}>{[...i.tags]}</div>
                ))}
            </div>
        </main>);
}

export default Category;
