import axios from "axios";
import { useEffect, useState } from "react";

const Categories = () => {

  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8000/post/tags").then((res) => setData(res.data))
  }, [])

  return (
    <>
      <div className="search-element">
        <input
          type="search"
          placeholder="Search..."
          onChange={(e) => setUserSearchInput(e.target.value)}
        />
      </div>

      <div className="feed-header">
        <h1>{userMessage}</h1>
        <div className="feed-header-group">
          <select
            defaultValue="New"
            name="sorting"
            id="sorting"
            onChange={sortItem}
          >
            <option value="New">Recent</option>
            <option value="Oldest">Oldest</option>
            <option value="Views">Most Viewed</option>
            <option value="Likes">Most Liked</option>
          </select>
          <ReactPaginate
            id="pagination"
            activeClassName={"item active "}
            breakClassName={"item break-me "}
            breakLabel={"..."}
            containerClassName={"pagination"}
            disabledClassName={"disabled-page"}
            marginPagesDisplayed={2}
            nextClassName={"item next "}
            nextLabel={">"}
            onPageChange={handlePageClick}
            pageCount={Math.ceil(data.length / itemsPerPage)}
            pageClassName={"item pagination-page "}
            pageRangeDisplayed={2}
            previousClassName={"item previous"}
            previousLabel={"<"}
            forcePage={currentPage}
          />
        </div>
      </div>
      <div className="feed-grid">

        {sortedData
          .filter((item) =>
            item.title.toLowerCase().includes(userSearchInput.toLowerCase())
          )
          .slice(indexOfFirstItem, indexOfLastItem)
          .map((element, index) => (

            <div key={index} className="feed-card">
              <Link
                to={`/post/${element._id}`}
                onClick={(e) => handleViews(element)}
              >
                <div className="feed-thumbnail-holder">
                  <div
                    className="feed-thumbnail"
                    style={{
                      backgroundImage: `url("http://localhost:8000/uploads/thumbnail/thumbnail-${element.img}")`,
                    }}
                  ></div>
                </div>
                <div className="feed-text">
                  <h3 className="feed-title">{element.title}</h3>
                  <div className="feed-analitics">
                    <h6>Views: {element.views}</h6>
                    <h6>Likes: {element.likes}</h6>
                  </div>
                </div>
              </Link>
            </div>
          ))}
      </div>
      <div className="result-pagination">
        <ReactPaginate
          id="pagination"
          activeClassName={"item active "}
          breakClassName={"item break-me "}
          breakLabel={"..."}
          containerClassName={"pagination"}
          disabledClassName={"disabled-page"}
          marginPagesDisplayed={2}
          nextClassName={"item next "}
          nextLabel={">"}
          onPageChange={handlePageClick}
          pageCount={Math.ceil(data.length / itemsPerPage)}
          pageClassName={"item pagination-page "}
          pageRangeDisplayed={2}
          previousClassName={"item previous"}
          previousLabel={"<"}
          forcePage={currentPage}
        />
      </div>
    </>
  );
};

export default Categories;
