import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Upload = () => {
  const [userData, setUserData] = useState({
    title: "",
    description: "",
    likes: 0,
    views: 0,
    date: Date.now(),
    image: "",
    creator: "",
  });

  const [imageFile, setImageFile] = useState({
    fileLocation: "",
    fileSize: "",
  });

  const [isSumbitted, setIsSubmitted] = useState("none");
  const [imagePreviewUrl, setImagePreviewUrl] = useState(null); // New state for image preview

  const navigate = useNavigate();

  useEffect(() => {
    setImageFile((prev) => ({
      ...prev,
      fileLocation: "http://localhost:8000/uploads/no-img.png",
    }));
  }, []);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setUserData((prev) => ({ ...prev, image: file }));
    // Create object URL and set it to the state for preview
    setImagePreviewUrl(URL.createObjectURL(file));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("title", userData.title);
    formData.append("desc", userData.description);
    formData.append("likes", userData.likes);
    formData.append("views", userData.views);
    formData.append("date", userData.date);
    formData.append("img", userData.image, userData.image.name);
    console.log(formData.get("image"));
    try {
      const result = await axios.post("http://localhost:8000/post/", formData);
      console.log(result);
    } catch (error) {
      console.error(error);
    }

    navigate("/uploaded");
  };

  return (
    <>
      <div style={{ display: isSumbitted }}></div>
      <main className="form-main">
        <form onSubmit={(e) => handleSubmit(e)}>
          {/* ... other form sections ... */}
          <section className="form-section-img">
            <div>
              <div>Choose your image:</div>
            </div>
            <div>
              <label className="upload-btn" htmlFor="file"></label>
              <input
                className="upload-btn-hidden"
                id="file"
                type="file"
                accept="image/*"
                onChange={handleImageChange} // Use the new handler
              />
            </div>
          </section>
          <section className="form-section-preview">
            <div>Preview</div>
            {imagePreviewUrl && (
              <div
                className="form-upload-preview"
                style={{
                  backgroundImage: `url(${imagePreviewUrl})`,
                }}
              ></div>
            )}
          </section>
          <input className="form-input" type="submit" value="Submit" />
        </form>
      </main>
    </>
  );
};

export default Upload;
