import { Navigate, useParams, useNavigate, Link } from "react-router-dom";

import { useEffect, useState } from "react";
import axios from "axios";

const PostDetail = () => {
  const { id } = useParams();
  const [post, setPost] = useState([]);
  const [postCreator, setPostCreator] = useState();

  const getPostData = async () => {
    try {
      const getPost = await axios.get("http://localhost:8000/post/" + id);
      setPost(getPost.data);
      const getCreator = await axios.get(
        "http://localhost:8000/user/creatorName"
      );
      setPost((prev) => ({ ...prev, creatorName: getCreator.data }));
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getPostData();
  }, []);

  console.log(post);
  return (
    <>
      <div className="Post-Detail">
        <main></main>
      </div>
    </>
  );
};

export default PostDetail;
