import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Register = () => {
  const [userData, setUserData] = useState({})
  const [displayAvatar, setDisplayAvatar] = useState(null)
  const navigate = useNavigate()

  const handleRegister = async (e) => {
    e.preventDefault()

    await setUserData((prev) => ({ ...prev, avatar: displayAvatar.name }))

    try {
      console.log(userData)
      await axios.post("http://localhost:8000/user/register", userData);

      navigate("/")
    } catch (error) {
      console.error(error)
    }


  }

  console.log(userData)
  return (
    <main>
      <main className="form-main">
        <h1 className="form-title">Register</h1>
        <form onSubmit={(e) => handleRegister(e)}>
          <section className="form-section-preview-avt">
            {displayAvatar && <div className="form-upload-preview-avt" style={{
              backgroundImage: `url(${URL.createObjectURL(displayAvatar)})`
            }}></div>}
          </section>
          <section className="form-section-img">
            <div>
              <div>Choose your avatar:</div>
            </div>
            <div>
              <label className="upload-btn" htmlFor="file"></label>
              <input
                className="upload-btn-hidden"
                id="file"
                type="file"
                accept="image/*"
                onChange={(e) => setDisplayAvatar(e.target.files[0])}

              />
            </div>
          </section>

          <section className="form-section">
            <div className="form-input-usr-img"></div>
            <input
              className="form-input"
              required
              type="text"
              placeholder="Username"
              value={userData.username}
              onChange={(e) =>
                setUserData((prev) => ({ ...prev, username: e.target.value }))
              }
            />
          </section>
          <section className="form-section">
            <div className="form-input-email-img"></div>
            <input
              className="form-input"
              required
              type="email"
              placeholder="Email"
              value={userData.email}
              onChange={(e) =>
                setUserData((prev) => ({ ...prev, email: e.target.value }))
              }
            />
          </section>
          <section className="form-section">
            <div className="form-input-psw-img"></div>
            <input
              className="form-input"
              required
              type="password"
              placeholder="Password"
              value={userData.password}
              onChange={(e) =>
                setUserData((prev) => ({ ...prev, password: e.target.value }))

              }
            />
          </section>
          <section className="form-section">
            <div className="form-input-psw-img"></div>
            <input
              className="form-input"
              required
              type="password"
              placeholder="Verify Password"
              value={userData.passwordAgain}
              onChange={(e) =>
                setUserData((prev) => ({ ...prev, passwordAgain: e.target.value }))

              }
            />
          </section>
          <input className="form-input" type="submit" value="Register" />
        </form>
      </main>
    </main>
  );
};

export default Register;
