import { useState } from "react";
import "./Forms.css";
import useFetch from "./useFetch";

const Login = () => {
  const { data, pending, error } = useFetch("http://localhost:8000/user/get");

  const [userData, setUserData] = useState({
    email: String,
    password: String,
  });

  const handleLogin = () => {
    data.forEach((element) => {
      if (
        element.email === userData.email &&
        element.password === userData.password
      ) {
        return 1;
      } else {
        return 0;
      }
    });
  };

  console.log(userData);

  return (
    <main>
      <main className="form-main">
        <form onSubmit={handleLogin}>
          <section className="form-section">
            <div className="form-input-email-img"></div>
            <input
              className="form-input"
              required
              type="email"
              placeholder="Email"
              value={userData.email}
              onChange={(e) =>
                setUserData((prev) => ({ ...prev, email: e.target.value }))
              }
            />
          </section>
          <section className="form-section">
            <div className="form-input-psw-img"></div>
            <input
              className="form-input"
              required
              type="password"
              placeholder="Password"
              value={userData.password}
              onChange={(e) =>
                setUserData((prev) => ({ ...prev, password: e.target.value }))
              }
            />
          </section>
          <input className="form-input" type="submit" value="Login" />
        </form>
      </main>
    </main>
  );
};

export default Login;
