import { useEffect, useState } from "react";

const useFetch = (url) => {
    const [data, setData] = useState(null)
    const [error, setError] = useState(null)
    const [pending, setPending] = useState(true)

    useEffect(()=> {
        fetch(url, {
            method: "GET",
        })
        .then((res) => res.json())
        .then ((items) => {
            setData(items);
            pending = false;
        })
        .catch((error) => {
            setError(error);
            setPending(false);
        },1000)
    },[url])

    return {data,error,pending}
}
 
export default useFetch;
