import axios from "axios";
import { useEffect, useState } from "react";
import "../../styles/TagMenu.scss"



const TagMenu = ({ isActive }) => {

    const [tag, setTag] = useState([])
    const [isActiveState, setIsActiveState] = useState(isActive);


    console.log(isActive)

    useEffect(() => {
        axios.get("http://localhost:8000/post/tags")
            .then((res) => {
                const newTag = Object.keys(res.data).map((key) => ({
                    tag: key,
                    img: [...res.data[key]]
                }));

                setTag(newTag);

            })
            .catch((err) => console.error("Error fetching data:", err));
    }, []);

    return (
        <div className="tag-menu">
            {isActive && <div>
                {isActive && <div onClick={(e) => { setIsActiveState(false) }} className="tag-menu-active tag-hamburger"></div>}
                <div className="tag-menu-body"></div></div>}
        </div>);
}

export default TagMenu;
