import UseScroll from "./UseScroll";
import { useEffect, useState } from "react";
import ReactPaginate from "react-paginate";
import { Link } from "react-router-dom";
import Header from "./Header";

const PostList = (data) => {
  const itemsPerPage = 16;
  const [currentPage, setCurrentPage] = useState(0);
  const [sortedData, setSortedData] = useState([]);
  const indexOfLastItem = (currentPage + 1) * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const [userMessage, setUserMessage] = useState("");
  const [userSearchInput, setUserSearchInput] = useState("");
  const [tempSearch, setTempSearch] = useState("")
  const [showSearchRecommendation, setShowSearchRecommendation] = useState(false);

  const handlePageClick = (selectedPage) => {
    setCurrentPage(selectedPage.selected);
    UseScroll("#root", "#pagination", 1000, "easeOutQuint");
  };

  const sortItem = (e) => {
    let sort = e ? e.target.value : "Views";
    switch (sort) {
      case "Likes": {
        setUserMessage("The most liked posts:");
        setSortedData((prev) => [...prev].sort((a, b) => b.likes - a.likes));
        break;
      }
      case "Views": {
        setUserMessage("The most viewed posts:");
        setSortedData((prev) => [...prev].sort((a, b) => b.views - a.views));
        break;
      }
      default: {
        setSortedData((prev) => [...prev]);
      }
    }
  };

  const onSearch = () => {
    console.log(tempSearch)
    setUserSearchInput(tempSearch)

  }

  const onSeaching = (e) => {
    let temp = e
    if (temp != "") {
      setShowSearchRecommendation(true)
      setTempSearch(temp)
    } else {
      setShowSearchRecommendation(false)
      set
    }
  }


  useEffect(() => {
    setSortedData((prev) => [...data.data]);
    sortItem();
  }, []);


  return (
    <>
      <div className="search-element">
        <input
          type="search"
          placeholder="Search..."
          onChange={(e) => onSeaching(e.target.value)}
        />
        <input
          className="search-btn"
          type="button"
          onClick={(e) => onSearch}
        />
        
      </div>
      <div className={`search-recommendation-${showSearchRecommendation}`}>
          
      </div>
      <div className="result-pagination">
        <div className="sort-element">
          <h2>{userMessage}</h2>
          <div className="sort-pagination">
            <select
              defaultValue="Views"
              name="sorting"
              id="sorting"
              onChange={sortItem}
            >
              <option value="Views">Most Viewed</option>
              <option value="Likes">Most Liked</option>
              <option value="New">Recent (wip)</option>
              <option value="Oldest">Oldest (wip)</option>
            </select>
            <ReactPaginate
              id="pagination"
              activeClassName={"item active "}
              breakClassName={"item break-me "}
              breakLabel={"..."}
              containerClassName={"pagination"}
              disabledClassName={"disabled-page"}
              marginPagesDisplayed={2}
              nextClassName={"item next "}
              nextLabel={">"}
              onPageChange={handlePageClick}
              pageCount={Math.ceil(data.data.length / itemsPerPage)}
              pageClassName={"item pagination-page "}
              pageRangeDisplayed={2}
              previousClassName={"item previous"}
              previousLabel={"<"}
              forcePage={currentPage}
            />
          </div>
        </div>
      </div>
      <div className="result-grid">
        {sortedData
          .filter((v) => v.name.includes(userSearchInput))
          .slice(indexOfFirstItem, indexOfLastItem)
          .map((element, index) => (
            <div key={index} className="result-grid-element">
              <Link to={`/post/${element.id}`}>
                <div className="result-element-container">
                  <div
                    className="result-img"
                    style={{
                      backgroundImage: `url("${element.imagePath}")`,
                    }}
                  ></div>
                  <div className="result-text">
                    <div className="result-title">{element.name}</div>
                    <h6>Views: {element.views}</h6>
                    <div className="result-analitics-container">
                      <div>Likes: {element.likes}</div>
                      <div>Added: {element.date}</div>
                    </div>
                  </div>
                </div>
              </Link>
            </div>
          ))}
      </div>
      <div className="result-pagination">
        <ReactPaginate
          id="pagination"
          activeClassName={"item active "}
          breakClassName={"item break-me "}
          breakLabel={"..."}
          containerClassName={"pagination"}
          disabledClassName={"disabled-page"}
          marginPagesDisplayed={2}
          nextClassName={"item next "}
          nextLabel={">"}
          onPageChange={handlePageClick}
          pageCount={Math.ceil(data.data.length / itemsPerPage)}
          pageClassName={"item pagination-page "}
          pageRangeDisplayed={2}
          previousClassName={"item previous"}
          previousLabel={"<"}
          forcePage={currentPage}
        />
      </div>
    </>
  );
};

export default PostList;
