import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import UseScroll from './UseScroll';
import SmoothScroll from "smooth-scroll";

const ScrollOnLocation = (target, anchor, speed, ease) => {
    const location = useLocation();

    var scroll = new SmoothScroll();
    var scrollOptions = { speed: speed, easing: ease }

    useEffect(() => {
        // Scroll down 200 pixels when the location changes
        scroll.animateScroll(document.querySelector(target), document.querySelector(anchor), scrollOptions)
    }, [location.pathname]); // Trigger the effect only when the pathname changes

    return null; // This component doesn't render anything
};

export default ScrollOnLocation;
