import { Navigate, useParams, useNavigate, Link } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

const PostDetail = () => {
  const { id } = useParams();
  const [postData, setPostData] = useState([]);
  const [imageData, setImageData] = useState({});

  const calculateImageSize = (originalWidth, originalHeight, maxWidth) => {
    const aspectRatio = originalWidth / originalHeight;
    const newHeight = (maxWidth * aspectRatio);

    return {
      width: `${maxWidth}vh`,
      height: `${newHeight}vh`,
    };
  };

  const getPostData = async () => {
    try {
      const { data: getPost } = await axios.get(
        "http://localhost:8000/post/" + id
      );
      setPostData(getPost.data);
    } catch (err) {
      console.log(err);
    }
  };

  const getImageDetails = async () => {
    try {
      const img = new Image();

      img.onload = function () {


        setImageData((prev) => ({ ...prev, width: this.width, height: this.height }))
      }
      img.src = 'http://localhost:8000/uploads/' + postData.post.img;
    } catch (err) {

    }
  }

  useEffect(() => {
    getPostData();
  }, []);

  useEffect(() => {
    getImageDetails()
  }, [postData])

  console.log(postData);
  return (
    <>
      {postData.post && (
        <div className="Post-Detail">
          <main>
            <div
              className="detail-image"
              style={{
                backgroundImage: `url("http://localhost:8000/uploads/${postData.post.img}")`,
                ...calculateImageSize(
                  imageData.width,
                  imageData.height,
                  80 // 80vh max width
                ),
              }}
            ></div>
            <div className="detail-text">
              <div><h1>{postData.post.title}</h1></div>
              <div className="detail-descripton">{postData.post.desc}</div>
              <div className="detail-reaction">
                <div>Views: {postData.post.views}</div>
                <div>Likes: {postData.post.likes}</div>
              </div>
              <div className="detail-creator">
                Created by:
                <div
                  className="detail-creator-avt"
                  style={{
                    backgroundImage: `url("http://localhost:8000/uploads/${postData.user.avatar}")`,
                  }}
                ></div>
                {postData.user.username}
              </div>
            </div>
          </main>
        </div>
      )}
    </>
  );
};

export default PostDetail;
