import { Navigate, useParams, useNavigate, Link } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

const PostDetail = () => {
  const { id } = useParams();
  const [post, setPost] = useState([]);

  const getPostData = async () => {
    try {
      const { data: getPost } = await axios.get(
        "http://localhost:8000/post/" + id
      );
      setPost(getPost);
      getPostCreator(); // Move it here
    } catch (err) {
      console.log(err);
    }
  };

  const getPostCreator = () => {
    try {
      const { data: getCreator } = axios.post(
        "http://localhost:8000/user/creatorName",
        { username: post.creator }
      );
      console.log(getCreator);
      setPost((prev) => ({ ...prev, creatorName: getCreator.data }));
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getPostData();
  }, []);

  return (
    <>
      <div className="Post-Detail">
        <main></main>
      </div>
    </>
  );
};

export default PostDetail;
