import { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import AuthContext from "../lib/AuthContext";

import PostList from "../posts/PostList";
import axios from "axios";
import Login from "../auth/Login";
import CategoryList from "../layout/CategoryList";

const Main = () => {
  const [posts, setPosts] = useState(null);
  const [categories, setCategories] = useState(null);
  const { loggedIn, getLoggedIn } = useContext(AuthContext);
  const navigate = useNavigate();

  async function getCustomers() {
    await getLoggedIn();
    try {
      const postsRes = await axios.get("http://localhost:8000/post/");
      const categoriesRes = await axios.get("http://localhost:8000/post/tags")
      setPosts(postsRes.data);
      setCategories(categoriesRes.data);
    } catch (err) {
      console.warn("You need to be logged in, to proceed: " + err);
      return navigate("/login");
    }

    navigate("/");
  }

  useEffect(() => {
    getCustomers();
  }, []);

  return (
    <main>
      {!posts && loggedIn && <div className="loading">Loading...</div>}
      {posts && <PostList data={posts} />}
      {posts && <CategoryList data={categories} />}
    </main>
  );
};

export default Main;
