import { useEffect, useState } from "react";
import ReactPaginate from "react-paginate";


const Main = () => {
  //init variables
  let currentSorting = 0;
  const itemsPerPage = 16;
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const indexOfLastItem = (currentPage + 1) * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
 const [currentItems, setCurrentItems] = useState([])
//handles the pagination to set the current elements depending on the page number
  const handlePageClick = (selectedPage) => {
    setCurrentPage(selectedPage.selected);
    document.body.screenTop = 0;
    document.documentElement.scrollTop = 0
    console.log(currentSorting);
  };

  const sortItem = () => {
    data.sort((a,b) => b.name-a.name)
  };

  useEffect(() => {
    fetch("http://localhost:8000/items", {
      method: "GET",
    })
      .then((res) => res.json())
      .then((items) => {
        setData(items)
        setLoading(false)
        console.log("Fetched:");
        console.log(data)
        console.log("------------------------------------------------------------------------------------------------------------------------")
        sortItem();
      })
      .catch((error) => console.error(error));

      
  }, []);

  return (
    <>

      <main>
        <div className="main-container">
          <div className="result-container">
            <div className="result-pagination">
              <div className="sort-element">
                <select defaultValue="AlphabeticAsc" value={currentSorting} name="sorting" id="sorting" onChange={sortItem}>
                  <option value="AlphabeticAsc">Alphabetical asc.</option>
                  <option value="AlphabeticDesc">Alphabetical desc.</option>
                  <option value="DescLikes">Most Liked</option>
                  <option value="DescViews">Most Viewed</option>
                </select>
              </div>
            </div>
            <div className="result-grid">
              {loading ? ("Loading...") : (
                
                data.slice(indexOfFirstItem,indexOfLastItem).map((element, index) => (
                <div key={index} className="result-grid-element">
                  <div className="result-element-container">
                    <div
                      className="result-img"
                      style={{
                        backgroundImage: `url("${element.imagePath}")`,
                      }}
                    ></div>
                    <div className="result-text">
                      <div className="result-title">{element.name}</div>
                      <div className="result-analitics-container">
                        <div>Likes: {element.likes}</div>
                        <div>Added: {element.date}</div>
                      </div>
                    </div>
                  </div>
                </div>
              )))}
            </div>
            <div className="result-pagination">
              <ReactPaginate
                activeClassName={"item active "}
                breakClassName={"item break-me "}
                breakLabel={"..."}
                containerClassName={"pagination"}
                disabledClassName={"disabled-page"}
                marginPagesDisplayed={2}
                nextClassName={"item next "}
                nextLabel={">"}
                onPageChange={handlePageClick}
                pageCount={Math.ceil(data.length / itemsPerPage)}
                pageClassName={"item pagination-page "}
                pageRangeDisplayed={2}
                previousClassName={"item previous"}
                previousLabel={"<"}
              />
            </div>
          </div>
        </div>
      </main>
    </>
  );
};

export default Main;
