import { useEffect, useState } from "react";
import ReactPaginate from "react-paginate";
import leftBtn from "./img/l-btn.png";

const Main = () => {
  const itemsPerPage = 16;
  const [data, setData] = useState({ items: [] });
  const [currentPage, setCurrentPage] = useState(0);

  const CreateData = () => {
    let newData = { items: [] };
    for (let i = 1; i < 51; i++) {
      let item = {
        imagePath: `https://picsum.photos/1000/1000?random=${i}`,
        views: Math.floor(Math.random() * 1000),
        likes: Math.floor(Math.random() * 100),
        name: `Picture of number ${i}`,
        date: "YYYY-MM-DD hh:mm",
      };

      newData.items.push(item);
    }
    setData((oldData) => ({ ...oldData, ...newData }));
  };

  useEffect(() => {
    return () => {
      CreateData();
    };
  }, []);

  const handlePageClick = (selectedPage) => {
    setCurrentPage(selectedPage.selected);
  };

  const indexOfLastItem = (currentPage + 1) * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = data.items.slice(indexOfFirstItem, indexOfLastItem);

  console.log(data);

  return (
    <>
      <main>
        <div className="main-container">
          <div className="result-container">
            <div className="result-pagination">
              <ReactPaginate
                previousLabel={leftBtn}
                nextLabel={">"}
                breakLabel={"..."}
                pageCount={Math.ceil(data.items.length / itemsPerPage)}
                marginPagesDisplayed={2}
                pageRangeDisplayed={5}
                onPageChange={handlePageClick}
                containerClassName={"pagination"}
                subContainerClassName={"pages-pagination"}
                activeClassName={"active"}
              />
            </div>
            <div className="result-grid">
              {currentItems.map((element, index) => (
                <div key={index} className="result-grid-element">
                  <div className="result-element-container">
                    <div
                      className="result-img"
                      style={{
                        backgroundImage: `url("${element.imagePath}")`,
                      }}
                    ></div>
                    <div className="result-text">
                      <div className="result-title">{element.name}</div>
                      <div className="result-analitics-container">
                        <div>Likes: {element.likes}</div>
                        <div>Added: {element.date}</div>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </main>
    </>
  );
};

export default Main;
